import React from 'react';
import './App.css';
import myAudio from './dababy.mp3';
import text from './text.txt'
function RepeatButton(props) {
  return (
    <button
      aria-label='Play again.'
      id='repeatButton'
      onClick={props.onClick}>
    </button>
  );
}

function WinningSound() {
  return (
    <audio autoPlay="autoplay" className="player" preload="false">
      <source src="./dababy.mp3" />
    </audio>
  );
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      winner: null,
      audio: new Audio(myAudio),
      bg1: null
    }
    this.finishHandler = this.finishHandler.bind(this)
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState({ winner: null });
    this.state.audio.play()
    this.emptyArray();
    this._child1.forceUpdateHandler();
    this._child2.forceUpdateHandler();
    this._child3.forceUpdateHandler();
  }

  static matches = [];

  finishHandler(value) {
    App.matches.push(value);

    if (App.matches.length === 2) {
      const first = App.matches[0];
      let results = App.matches.every(match => match === first)
      this.setState({ winner: results });
    }
  }
  emptyArray() {
    App.matches = [];
  }

  render() {
    const { winner } = this.state;
    let repeatButton = null;
    let winningSound = null;

    if (winner !== null) {
      repeatButton = <RepeatButton onClick={this.handleClick} />
    }

    if (winner) {
      winningSound = <WinningSound />
    }

    return (
      <div>
        {winningSound}
        <div className={`spinner-container`}>
          <Spinner onFinish={this.finishHandler} ref={(child) => { this._child1 = child; }} timer="1000" />
          <Spinner onFinish={this.finishHandler} ref={(child) => { this._child2 = child; }} timer="1400" />
          <Spinner onFinish={this.finishHandler} ref={(child) => { this._child3 = child; }} timer="1700" />
          <div className="gradient-fade"></div>
        </div>
        {repeatButton}
      </div>
    );
  }
}

class Spinner extends React.Component {
  constructor(props) {
    super(props);
    this.forceUpdateHandler = this.forceUpdateHandler.bind(this);
    this.state={
      text:['']}
  };

  forceUpdateHandler() {
    this.reset();
  };

  reset() {
    if (this.timer) {
      clearInterval(this.timer);
    }

    this.start = this.setStartPosition();

    this.setState({
      position: this.start,
      timeRemaining: this.props.timer
    });

    this.timer = setInterval(() => {
      this.tick()
    }, 100);
  }

  state = {
    position: 0,
    lastPosition: null
  }
  static iconHeight = 188;
  multiplier = Math.floor(Math.random() * (4 - 1) + 1);

  start = this.setStartPosition()+5900;
  speed = Spinner.iconHeight * this.multiplier;

  setStartPosition() {
    return ((Math.floor((Math.random() * 9))) * Spinner.iconHeight) * -1;
  }

  moveBackground() {
    this.setState({
      position: this.state.position - this.speed,
      timeRemaining: this.state.timeRemaining - 100
    })
  }

  getSymbolFromPosition() {
    const totalSymbols = 9;
    const maxPosition = (Spinner.iconHeight * (totalSymbols - 1) * -1);
    let moved = (this.props.timer / 100) * this.multiplier
    let startPosition = this.start;
    let currentPosition = startPosition;

    for (let i = 0; i < moved; i++) {
      currentPosition -= Spinner.iconHeight;

      if (currentPosition < maxPosition) {
        currentPosition = 0;
      }
    }

    this.props.onFinish(currentPosition);
  }

  tick() {
    if (this.state.timeRemaining <= 0) {
      clearInterval(this.timer);
      this.getSymbolFromPosition();

    } else {
      this.moveBackground();
    }
  }

  // async componentDidMount() {
  //   let txt = await fetch(text)
  //     .then(response => response.text());
  //   console.log(txt);
  //   let bg1 = <svg height="564px" width="128px">{txt.split(/\r?\n/).map((e, i) => { return <text key={i} x='0' y={i * 50} fill='black' fontSize='20'>{e}</text> })}</svg>;
  //   console.log('url("data:image/svg+xml;utf8,' + ReactDOMServer.renderToStaticMarkup(bg1) + '")')
  //   document.getElementsByClassName('icons').style.background = 'red';
  //   // document.getElementsByClassName('icons').style.background = 'url("data:image/svg+xml;utf8,' + ReactDOMServer.renderToStaticMarkup(bg1) + '")';
  // }
  async componentDidMount() {
    clearInterval(this.timer);

    this.setState({
      position: this.start,
      timeRemaining: this.props.timer
    });

    this.timer = setInterval(() => {
      this.tick()
    }, 100);

    let txt = await fetch(text)
      .then(response => response.text());
    let arrayText = txt.split(/\r?\n/);
    this.setState({text:arrayText})
  }

  render() {
    let { position } = this.state;

    return (
      <div>
        <div
          style={{ top:position}}
          className={`icons`}
        >{this.state.text!==null?(this.state.text.map((e, i) => { return <p key={i}>{e}</p> })):''}</div>
        {}
      </div>
    )
  }
}

export default App;
